      scale: 15,
      autoAlpha: 15,
      duration: 1,
      ease: "power2",
      transformOrigin: "50% 50%",
      repeat: -1,
      repeatDelay: 4,
      delay: 2.5,
    }
  );
  gsap.to("#splash2", {
    opacity: 0,
    duration: 1,
    repeat: -1,
    repeatDelay: 4,
    delay: 3,
  });
  gsap.fromTo(
    "#splash3",
    {
      opacity: 0,
      scale: 0,
      autoAlpha: 0,
      duration: 1,
      ease: "power2",
      transformOrigin: "50% 50%",
      repeat: -1,
      repeatDelay: 4,
      delay: 4,
    },
    {
      opacity: 1,
      scale: 15,
      autoAlpha: 15,
      duration: 1,
      ease: "power2",
      transformOrigin: "50% 50%",
      repeat: -1,
      repeatDelay: 4,
      delay: 4,
    }
  );
  gsap.to("#splash3", {
    opacity: 0,
    duration: 1,
    repeat: -1,
    repeatDelay: 4,
    delay: 4.5,
  });
  Summer.to("#lcloud1", { xPercent: -140 }, 0);
  Summer.to("#lcloud2", { xPercent: -160 }, 0);
  Summer.to("#lcloud3", { xPercent: -120 }, 0);
  Summer.to("#rcloud1", { xPercent: 140 }, 0);
  Summer.to("#rcloud2", { xPercent: 160 }, 0);
  Summer.to("#rcloud3", { xPercent: 120 }, 0);

  const off = gsap.timeline();
  scrollTrigger3 = ScrollTrigger.create({
    animation: off,
    trigger: ".scrollElement",
    start: "44% top",
    end: "54% 100%",
    scrub: 3,
  });
  off.to("#off", { opacity: 0 }, 0);
});

onBeforeUnmount(() => {
  scrollTrigger1.kill();
  scrollTrigger2.kill();
  scrollTrigger3.kill();
});
</script>

