<script setup>
import { ref, reactive, onMounted, watch } from "vue";
import Squiggly from "@/components/Squiggly";
import gsap from "gsap";

const wordRef = ref();
const group = ref();
const temp = ref(0);
const warningRef = ref();

let delay = 0;
const wordAni = gsap.timeline({ paused: true });

const mouse = reactive({
  x: -100,
  y: -100,
});

const onMouseMove = (e) => {
  mouse.x = (e.pageX - window.innerWidth / 2) / window.innerWidth;
  mouse.y = (e.pageY - window.innerHeight / 2) / window.innerHeight;

  temp.value = Math.max(Math.abs(mouse.x), Math.abs(mouse.y));
  temp.value = Math.round(temp.value * 100) * 0.02;
};

const onTouchMove = (e) => {
  mouse.x = (e.touches[0].pageX - window.innerWidth / 2) / window.innerWidth;
  mouse.y = (e.touches[0].pageY - window.innerHeight / 2) / window.innerHeight;

  temp.value = Math.max(Math.abs(mouse.x), Math.abs(mouse.y));
  temp.value = Math.round(temp.value * 100) * 0.02;
};

watch(temp, () => {
  if (temp.value > 0.3) {
    wordAni.progress(Math.pow((temp.value - 0.3) / 0.7, 2));
    if (temp.value > 0.6) {
      warningRef.value.style.opacity = Math.pow((temp.value - 0.6) / 0.4, 2);
    } else {
      warningRef.value.style.opacity = 0;
    }
  } else {
    wordAni.progress(0);
  }
});

onMounted(() => {
  const items = document.querySelectorAll(".word > path");
  items.forEach((item) => {
    const length = item.getTotalLength();
    for (let i = 0; i < length; i++) {
      const pointLength = Math.random() * length;
      const point = item.getPointAtLength(pointLength);
      const circle = document.createElementNS(
        "http://www.w3.org/2000/svg",
        "circle"
      );
      circle.setAttribute("cx", point.x);
      circle.setAttribute("cy", point.y);
      circle.setAttribute("r", Math.random() * 0.75 + 0.5);
      circle.setAttribute("fill", "white");
      group.value.appendChild(circle);

      wordAni.to(
        circle,
        {
          cx: point.x + (Math.random() - 0.5) * 50,
          cy: point.y + (Math.random() - 0.5) * 50,
          opacity: 0,
          delay: (delay + pointLength) * 0.001,
        },
        0
      );
    }
  });
});
</script>

<style lang="scss" scoped>
.container {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: calc(var(--vh) * 100);
  background: rgb(22, 22, 22);
  overflow: hidden;
  .bed {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 40%;
    object-fit: cover;
    pointer-events: none;
  }
  .warning {
    position: absolute;
    bottom: 10%;
    left: 50%;
    transform: translate(-50%);
    width: 30%;
    opacity: 0;
    z-index: 1;
    pointer-events: none;
  }
  .word {
    position: relative;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    animation: squiggly 0.5s linear infinite;
    pointer-events: none;
    path {
      opacity: 0;
    }
  }
}

@keyframes squiggly {
  0% {
    filter: url("#squiggly1");
  }
  25% {
    filter: url("#squiggly2");
  }
  50% {
    filter: url("#squiggly3");
  }
  75% {
    filter: url("#squiggly4");
  }
  100% {
    filter: url("#squiggly5");
  }
}
</style>
